mapper.select:
select * from(select zzz.*, rownum RNUM from( select emp_no||'' as "emp_no" ,emp_name as "emp_name" ,(select d.dep_name from dept d where d.dep_no=e.dep_no) as "dep_name" ,(select j.jikup from code_jikup j where j.jikup_cd=e.jikup_cd) as "jikup" from employee e where 1=1
order by ${sort}
order by emp_no asc
) zzz ) where RNUM>=${selectPageNo*rowCntPerPage-rowCntPerPage+1} and RNUM<=${selectPageNo*rowCntPerPage}

mapper.insert:
insert into employee (emp_no, emp_name, emp_eng_name, dep_no, jikup_cd, salary, jumin_num, emp_gender, phone, emp_email, emp_email_office, mgr_emp_no, emp_addr, worktime_cd, emp_pwd
) values (
(select nvl(max(emp_no), 0)+1 from employee)
,'${emp_name}'
,'${emp_eng_name}'
,${dep_no}
,${jikup_cd}
,${salary}
,'${jumin_num}'
,'${emp_gender}'
,'${phone}'
,'${emp_email}'
,'${emp_email_office}'
,(select emp_no from employee where emp_name='${mgr_emp_name}')
,'${emp_addr}'
,${worktime_cd}
,'${emp_pwd}'
)
