mapper.select:
select count(*) from ( select c.jikup as "jikup" ,e1.emp_name as "emp_name" ,d.dep_name as "dep_name" ,w.destination as "destination" ,w.work_outside_reason as "work_outside_reason" ,to_char(w.outside_start_time,'yyyy-mm-dd am hh12:mi:ss (dy)','NLS_DATE_LANGUAGE=korean') as "outside_start_time" ,to_char(w.outside_end_time,'yyyy-mm-dd am hh12:mi:ss (dy)','NLS_DATE_LANGUAGE=korean') as "outside_end_time" ,nvl(e2.emp_name,' ') as "mgr_name" from work_outside_info w inner join employee e1 on w.emp_no=e1.emp_no inner join code_jikup c on c.jikup_cd=e1.jikup_cd inner join dept d on d.dep_no=e1.dep_no left outer join employee e2 on e2.emp_no=e1.mgr_emp_no where 1=1
)
where 1=1
